mapper.update:
UPDATE FREEBOARD
SET F_STATUS = 'N'  	
WHERE FID = #{fId}

UPDATE FREEBOARD
SET FCOUNT = FCOUNT+1
WHERE FID = #{fId}

UPDATE FREEBOARD
SET REPLYCOUNT = REPLYCOUNT+1
WHERE FID = #{fId}

UPDATE FREEBOARD
SET REPLYCOUNT = REPLYCOUNT-1
WHERE FID = #{refFBId}

UPDATE FREEBOARD
SET FTITLE = #{fTitle}, FCONTENT=#{fContent}, F_MODIFY_DATE=SYSDATE, 
F_ORIGINAL_FILENAME = #{fOriginalFileName}, F_RENAME_FILENAME=#{fRenameFileName}
WHERE FID=#{fId}

UPDATE FREEBOARDREPLY
SET FBR_CONTENT =#{fbrContent}, FBR_MODIFY_DATE = SYSDATE
WHERE FBRID= #{fbrId}

UPDATE FREEBOARDREPLY
SET FBR_STATUS = 'N'
WHERE FBRID=#{fbrId}
